import { Component, OnInit } from '@angular/core';
import * as Highcharts from 'highcharts';


// //@ts-ignore
// import { FoamTree } from "../../../../node_modules/@carrotsearch/foamtree";

@Component({
  selector: 'app-search-page',
  templateUrl: './search-page.component.html',
  styleUrls: ['./search-page.component.scss'],
})
export class SearchPageComponent implements OnInit {
  query: string = '';
  constructor() {}
  // foamtree = new FoamTree({ 
  //   id: "visualization",
  //   dataObject: {
  //     groups: [
  //       { label: "Your", weight: 1.0 },
  //       { label: "First", weight: 3.0 },
  //       { label: "FoamTree", weight: 2.0 },
  //       { label: "Visualization", weight: 4.0 }
  //     ]
  //   } });
  Highcharts = Highcharts;
  linechart: any = {
    series: [
      {
        data: [1, 2, 3],
      },
    ],
    chart: {
      type: 'line',
    },
    title: {
      text: 'linechart',
    },
  };
  ngOnInit(): void {
    this.page_render();
  }

  public page_render() {
    console.log('page render');
    const el = document.getElementById('input_query') as HTMLInputElement;

    el?.addEventListener('keypress', (event) => {
      console.log('EL' + el.value);
      if (event.code === 'Enter') {
        this.cluster_search(JSON.stringify(el.value));
      }
    });

  }

  public cluster_search(query: any) {
    console.log('cluster search call query' + query);
  }
}
