import { Component, OnInit } from '@angular/core';


@Component({
  selector: 'app-search-page',
  templateUrl: './search-page.component.html',
  styleUrls: ['./search-page.component.scss'],
})
export class SearchPageComponent implements OnInit {

  query:string="";
  constructor() {}

  ngOnInit(): void {
    this.page_render()

  }

  public page_render(){
   console.log("called")
   var el = document.getElementById("input_query") ;
  //  const qn = document.getElementById("query_name"); 
  el?.addEventListener('keypress',(event)=>{
  console.log("new change"+el?.ariaValueNow)
  // this.query=this.query+''+event.key; 
  // this.query=this.query+''+event.key; 
  console.log(event.keyCode)
  if(event)
  if(event.code==="Enter"){
    console.log("last"+this.query.slice(0,-5));
    this.cluster_search(JSON.stringify(this.query))
  }
  else{
    this.query=this.query+''+event.key; 
  }
  })
  //  document.getElementById("input_query").addEventListener('change', ()=>{this.page_render()})
  //  console.log(document.getElementById()?.nodeValue)
  }


  public cluster_search(query:any){
     console.log("cluster search call query"+  query)
  }
}
