import { stringToFileBuffer } from '@angular-devkit/core/src/virtual-fs/host';
import { Component, OnInit } from '@angular/core';
import * as Highcharts from 'highcharts/highcharts-more';

@Component({
  selector: 'app-search-page',
  templateUrl: './search-page.component.html',
  styleUrls: ['./search-page.component.scss'],
})
export class SearchPageComponent implements OnInit {
  query: string = '';
  bubblechart: any;
  constructor() {}
  // Highcharts = Highcharts;
  // linechart: any = {
  //   series: [
  //     {
  //       data: [1, 2, 3],
  //     },
  //   ],
  //   chart: {
  //     type: 'line',
  //   },
  //   title: {
  //     text: 'linechart',
  //   },
  // };
  ngOnInit(): void {
    this.page_render();
  }

  public page_render() {
    console.log('page render');
    const el = document.getElementById('input_query') as HTMLInputElement;

    el?.addEventListener('keypress', (event) => {
      console.log('EL' + el.value);
      if (event.code === 'Enter') {
        this.cluster_search(JSON.stringify(el.value));
      }
    });
  }

  public cluster_search(query: any) {
    console.log('cluster search call query' + query);

    this.highchart_function();
  }

  public highchart_function() {
    this.bubblechart = {
      series: [
        {
          name: 'Europe',
          data: [{ name: 'Germany', value: 245 }],
        },
      ],
      chart: {
        type: 'packedbubble',
      },
      title: {
        text: 'Query Search',
      },
    };
  }
}
